mapper.select:
select
id, user_id, title, picture, description, start_time, end_time, location, limit, clicknum, number, likes
from activity


        and id = #{id}


        and user_id = #{userId}


        and title = #{title}


        and picture = #{picture}


        and description = #{description}


        and start_time = #{startTime}


        and end_time = #{endTime}


        and location = #{location}


        and `limit` = #{limit}


        and clicknum = #{clicknum}


        and number = #{number}


        and likes = #{likes}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from activity


        and id = #{id}


        and user_id = #{userId}


        and title = #{title}


        and picture = #{picture}


        and description = #{description}


        and start_time = #{startTime}


        and end_time = #{endTime}


        and location = #{location}


        and `limit` = #{limit}


        and clicknum = #{clicknum}


        and number = #{number}


        and likes = #{likes}
