mapper.select:
select
id, time, userName, password, avatar, name, sex, phone, email, description
from user


        and id = #{id}


        and time = #{time}


        and userName = #{username}


        and password = #{password}


        and avatar = #{avatar}


        and name = #{name}


        and sex = #{sex}


        and phone = #{phone}


        and email = #{email}


        and description = #{description}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from user


        and id = #{id}


        and time = #{time}


        and userName = #{username}


        and password = #{password}


        and avatar = #{avatar}


        and name = #{name}


        and sex = #{sex}


        and phone = #{phone}


        and email = #{email}


        and description = #{description}
